config:
{
  "bot_name": "Sniper Calls Bot",
  "version": "1.0",
  "license_key": "7G6D-3K8L-F9T2-W4JX",
  "settings":
    {
      "target_precision": 0.95,
      "max_calls_per_hour": 50,
      "call_delay_seconds": 5,
      "retry_attempts": 3
    },
  "api_config":
    {
      "base_url": "https://api.snipercalls.com",
      "endpoints":
        {
          "initiate_call": "/v1/calls/initiate",
          "status_check": "/v1/calls/status",
          "terminate_call": "/v1/calls/terminate"
        },
      "headers":
        {
          "Authorization": "Bearer YOUR_API_TOKEN",
          "Content-Type": "application/json"
        }
    },
  "logging":
    {
      "level": "INFO",
      "log_to_file": true,
      "log_file_path": "/var/logs/sniper_calls_bot.log"
    },
  "notifications":
    {
      "email":
        {
          "enabled": true,
          "smtp_server": "smtp.mailtrap.io",
          "smtp_port": 587,
          "username": "your_username",
          "password": "your_password",
          "from_address": "bot@snipercalls.com",
          "to_address": "admin@snipercalls.com"
        },
      "sms":
        {
          "enabled": false,
          "twilio_account_sid": "your_twilio_account_sid",
          "twilio_auth_token": "your_twilio_auth_token",
          "from_number": "+1234567890",
          "to_number": "+0987654321"
        }
    }
}
